import React, { useState } from "react";
import "./Css/Dashboard.css";
import Addpage from "./Addpage";
import axios from "axios";
function Dashboard() {
  let [studentdata, setstudentdata] = useState([]);
  const getStudentList = ()=>{

    axios.get("http://localhost:5000/").then((res) => {
      //console.clear();
      //console.log(res.data);
      setstudentdata(res.data);
  })
  };
  getStudentList();
  const showdashboard = () => {
    document.querySelector(".overlay")?.classList.remove("hidden");
    document.querySelector(".overlay")?.addEventListener("click", function (e) {
      if(
        e.target.className.includes("close-btn")||
        e.target.className.includes("overlay")
      )
       document.querySelector(".overlay")?.classList.add("hidden");
    });
  };

  const studentdelete = (id)=>{
    document.querySelector(".delete-overlay")?.classList.remove("hidden");
    // const deleteObj = {id:id};
    // console.log(deleteObj);
    axios.delete("http://localhost:5000/", {
      data:{
        id,
      }
    })
    .then((res)=>{
      console.log(res.data.message);
      getStudentList();
      document.querySelector(".delete-overlay").classList.add("hidden");
    },
    (err)=>{
      console.log(err);
    }
    );
  };


  return (
    <div className="Main">
      <div className="delete-overlay hidden">
        <p>Deleting student data..</p>
      </div>
      <div className="overlay hidden">
        <Addpage></Addpage>
      </div>
      <div className="Header">
        <div>
          <b>Student List</b>
        </div>
        <div className="btn-Add">
          <button onClick={showdashboard}>
            <i class="fas fa-user-plus"></i>Add
          </button>
        </div>
      </div>
      <div>
        <div>
          <div className="Main-table">
            <div class="table-header table-row">
              <p>S.No</p>
              <p>Name</p>
              <p>Email</p>
              <p>Qualification</p>
              <p>Created On</p>
              <p>Action</p>
            </div>
            <div class="table-content">
              {studentdata.map((student, index) => {
                const date = new Date(student.createdon);
                return (
                  <div class="table-row">
                    <p>{index + 1}</p>
                    <p>{student.name} </p>
                    <p>{student.email}</p>
                    <p>{student.qualification}</p>
                    <p>
                      {date.getDate()}/{date.getMonth()}/{date.getFullYear()}
                    </p>
                    <p>
                      <div>
                        <button className="btn">
                          {" "}
                          <i className="fas fa-user-edit"></i>
                        </button>
                        <button className="btn" onClick={studentdelete.bind(null,student.id)}>
                          
                          <i className=" icon-delete fas fa-user-slash"></i>
                        </button>
                      </div>
                    </p>
                  </div>
                );
              })}
              <p>
                {studentdata.length===0 ? "no student record found" : ""}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
