import React, { Component } from "react";
import axios from 'axios';
import "./Css/Addpage.css";
import { toast } from "react-toastify";
class Addpage extends Component {
  constructor(props){
    super(props)

    //console.log(props.state);
    this.state=props.state.state;
  }


  handleSubmit(event) {
    event.preventDefault();
    const data = new FormData(event.target);
    var error = document.getElementByc("error")
        if (isNaN(document.getElementById("number").value)) 
        {
              
            // Changing content and color of content
            error.textContent = "Please enter a valid number"
            error.style.color = "red"
        } else {
            error.textContent = ""
        }

if(data.get("name") == "" || data.get("email") == "" || data.get("qualification") == "")
{
  alert(data.get("name")+ "required");
  return 0;
}

    if(!((data.get("name")).charCodeAt(0) >=65 && (data.get("name")).charCodeAt(0) <=90))
    {
      alert("first letter should capital");
      return 0;
    }
     
    var obj = {
                             
      name: data.get("name"),
      email: data.get("email"),
      qualification: data.get("qualification"),
    };

    
  
    document.querySelector(".loading-overlay").classList.remove("hidden");


    
    //post data
    if (!data.get('id')) {
      document.querySelector(".loading-overlay p").textContent = "Adding Student Data...";
      axios.post("http://localhost:5000/", obj).then((res) => {
       // console.log(this);
       //console.log(res);
       toast.success(res.data.message, {autoClose:2000})
        this.props.state.getStudentList();


        
      //  console.log(res);
      document.querySelector(".loading-overlay").classList.add("hidden");
      event.target.closest(".overlay").classList.add("hidden");
    }).catch(error=>{
     // console.log(error);
      document.querySelector(".loading-overlay").classList.add("hidden");
      event.target.closest(".overlay").classList.add("hidden");
      toast.error(error.message, {autoClose:2000});
    });
    
  
    
    //updating data
} else  {
    obj.id = data.get('id');
    document.querySelector(".loading-overlay p").textContent = "Updating Student Data...";
    axios.put("http://localhost:5000/", obj).then(res => {
      this.props.state.getStudentList();
      document.querySelector(".loading-overlay").classList.add("hidden");
      event.target.closest(".overlay").classList.add("hidden");
    })
  }
  

}


  render() {
    return (
      //  <form>
      <form onSubmit={this.handleSubmit.bind(this)} autoComplete="off">
        <div className="loading-overlay hidden">
          <p>Adding Student Data...</p>
        </div>
        <div className="Add-user">
          <button type="button" className="close-btn" title="close">
            &times;
          </button>

          <div className="form">

          <div className="Error-Field">


          </div>
            <div className="Detail-requried">
              <label>
                Name*<span className="ast"></span>
              </label>

              <input
                type="text"
                name="name"
                id="name"
                placeholder="Name"
                 pattern = "[a-zA-Z]+ [a-zA-Z]+$"
                autoComplete="false"
                maxLength="20"
              />
            </div>
            <div className="Detail-requried">


              <input
                type="hidden"
                name="id"
                id="id"
                placeholder="id"
                required
                autoComplete="false"
              />
            </div>
            <div className="Detail-requried">
              <label>Email*</label>
              <input
                type="email"
                name="email"
                id="name"
                placeholder="Email"
                // required pattern="[^.]+@[^.]+"
                pattern ="[a-zA-Z0-9]+@(?:[a-zA-Z0-9]+\.)+[A-Za-z]+$"
                autoComplete="false"
              />
            </div>
            <div className="Detail-requried">
              <label>Qualification*</label>
              <input
                type="text"
                name="qualification"
                id="name"
                placeholder="Qualification"
                
                autoComplete="false"
              />
            </div>

            <div className="reset-save Detail-requried">
              <button className="btn" type='submit'>Submit</button>
              <div>
                <button className="btn1" type="reset">Reset</button>
              </div>
            </div>
          </div>
        </div>
      </form>
    );
  }
}

export default Addpage;
