  //         document.querySelector(".delete-overlay").classList.add("hidden");
  //       },
  //       (err) => {
  //         console.log(err);
  //       }
  //     );
  // }else if(event.target.className.includes("no")){
  //   document.querySelector(".delete-overlay").classList.add("hidden");
  // }
}))

};




